function headerMap(resp){
	const arr = resp.target.getAllResponseHeaders().trim().split(/[\r\n]+/);
	const _headerMap = {};
	arr.forEach((line) => {
		const parts = line.split(': ');
		const header = parts.shift();
		const value = parts.join(': ');
		_headerMap[header] = value;
	});
	return _headerMap
}

